import React, { useContext } from "react";

import { NavLink } from "react-router-dom";
import { AuthContext } from "../../shared/context/auth.context";

import "../../../styles/header.scss";

export const NavLinkItem: React.FC = () => {
  const auth = useContext(AuthContext);
  const { isLoggedIn } = auth;
   const linksItems = [
    { to: "/", label: "All Users", exact: true },
    { to: "/places/new", label: "Add Place", exact: false, auth: true },
    { to: "/places",label:"My Places",exact:false,auth:true},
    { to: "/auth", label: "Login", exact: false, auth: false },
    { to: "/auth", label: "Logout", exact: false, auth: true },
   ];
  const linksItemAuthed=[
    { to: "/", label: "All Users", exact: true },
    { to: "/places/new", label: "Add Place", exact: false, auth: true },
    { to: "/places",label:"My Places",exact:false,auth:true},
    { to: "/auth", label: "Logout", exact: false, auth: true },
  ]
  const linksItemNotAuthed=[
    { to: "/", label: "All Users", exact: true },
    { to: "/auth", label: "Login", exact: false, auth: false },
  ]
  const showLinks = (arrLinks) => (
    React.Children.toArray(
      arrLinks.map((link) => (
        <li>
          <NavLink
            to={link.to}
            className={(navData)=>navData.Active ? "active" : ""}
            >
            {link.label}
            </NavLink>
        </li>
      ))
    )
  )
  const linksItem=()=>(
    isLoggedIn?linksItemAuthed:linksItemNotAuthed
  )
  return (
    <ul className="nav-links">
      {
        React.Children.toArray(
          linksItems.map((item) => (
            <>
            {!item.auth?<li>
              <NavLink
                to={item.to}
                className={(navData)=>navData.isActive?"active":""}>
                {item.label}
                </NavLink>
            </li>:(
              isLoggedIn&&(
                <li>
                  <NavLink
                    onClick={()=>item.label==="Logout"?auth.logout():undefined}
                    to={item.to}
                    className={(navData)=>navData.isActive?"active":""}>
                    {item.label}
                    </NavLink>
                </li>    
              )
            )}
            </>
          ))
        )
      }
    </ul>
  );
};
