import React from 'react'
import { useParams } from 'react-router-dom';
import { useForm } from '../../app/hooks/useForm';
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE, VALIDATOR_EMAIL } from '../../utlis/validations/validator';
import Button from '../shared/UIElements/Button';
import Card from '../shared/UIElements/Card';
import { Input } from '../shared/UIElements/Input';

export const Auth:React.FC = () => {

    const [loading,setLoading]=React.useState(true)
    const [formState, inputHandler, setFormData] = useForm(
      {
        title: {
          value: "",
          isValid: false,
        },
        description: {
          value: "",
          isValid: false,
        },
      },
      false
    );

    React.useEffect(() => {
        setFormData({
            title: {
              value: "",
              isValid:  false,
            },
            description: {
              value:"",
              isValid: false,
            },
          },true)
          setLoading(false)
    },[setFormData])
    const placeSubmitHandler = (event: React.FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      console.log("formState", formState); //send this to back end
    };

    if(loading) {
      return (<div className="center">
        <Card>
          <h2>Loading...</h2>
        </Card>
      </div>)
    }
    if(false) {
      return (<div className="center">
        <Card>
          <h2 className="text-xl">Place not found</h2>
        </Card>
        </div>)
    }
      return (
          <form className="place-form" onSubmit={placeSubmitHandler}>
            <Input
              id="email"
              element="input"
              label="E-mail"
               type='email' 
              validators={[VALIDATOR_REQUIRE(),VALIDATOR_EMAIL()]}
              errorText="Please Enter A valid Email"
              onInput={inputHandler}
            //   inputValue={formState.inputs.title.value}
            //   inputValid={formState.inputs.title.isValid}

            />
            <Input
              id="email"
              element="input"
              label="Password"
               type='password' 
              validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(7)]}
              errorText="Please Enter A valid Email"
              onInput={inputHandler}
            //   inputValue={formState.inputs.title.value}
            //   inputValid={formState.inputs.title.isValid}

            />


            <Button type="submit" disabled={!formState.isValid} inverse={true}>
              submit
            </Button>
          </form>
        )
}
