import React from 'react'

import * as ReactDOM from 'react-dom'
import  '../../../styles/header.scss';

interface DrawerProps{
    children?: React.ReactNode;
    className? : string
    el? : string
}
  export const SideDrawerNavigation: React.FC<DrawerProps> = ({
    children,className,el = 'div'
  }): JSX.Element | null => {
    const [container] = React.useState(document.createElement(el))
    // const [container] = React.useState(drawerHook)
    if ( className )
        container.classList.add(className)
    React.useEffect(() => {
        document.body.appendChild(container)
        container.setAttribute('id', 'drawer-hook')
        return () => {
            document.body.removeChild(container)
        }
    }, [])
    let content = <aside className="side-drawer">{children}</aside>;
    return ReactDOM.createPortal(content, container)
  };
