import React from "react";
import '../../styles/users-list.scss';
import Avatar from "../shared/UIElements/Avatar";
import { User } from './userInterfaces';


export const UsersItem: React.FC<User> = ({name,id,image,placesCount}) => {
    return (
        <li className="user-item">
            <div className="user-item_content">
                <div className="user-item__image">
                    <Avatar 
                        image={image}
                        alt={name}
                        width={50}
                        className="user-item__image"
                        style={ {borderRadius: '50%'} }
                    />
                    <img src={image} alt={name}/>
                </div>   
                <div className="user-item__info">
                    <h2 className="user-item__name">{name}</h2>
                    <h3 className="user-item__places">{placesCount} {placesCount!==1?'places':'place'}</h3>
                 </div>   
            </div>
        </li>
    );
}
