import React from 'react'
import { useParams } from 'react-router-dom';
import { Input } from '../shared/UIElements/Input';
import { Place } from './placesInterFace';
import { VALIDATOR_MINLENGTH, VALIDATOR_REQUIRE } from '../../utlis/validations/validator';
import Button from '../shared/UIElements/Button';
import '../../styles/places.scss'
import { useForm } from '../../app/hooks/useForm';

const places:Place[] = [
    {
      id: 'p1',
      title: 'Empire State Building',
      description: 'One of the most famous sky scrapers in the world!',
      imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/df/NYC_Empire_State_Building.jpg/640px-NYC_Empire_State_Building.jpg',
      address: '20 W 34th St, New York, NY 10001',
      creatorId: 'u1',
      coordinates: {
        lat: '40.7484405',
        lng: '-73.9878584'
      }
    },{
      id: 'p2',
      title: 'Empire State Building 2',
      description: 'One of the most famous sky scrapers in the world! 2' ,
      imageUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/df/NYC_Empire_State_Building.jpg/640px-NYC_Empire_State_Building.jpg',
      address: '20 W 34th St, New York, NY 10001',
      creatorId: 'u2',
      coordinates: {
        lat: '40.7484405',
        lng: '-73.9878584'
      }
    }
  ]
  const [formState,inputHandler]=useForm({
      title: {
          value: place ? place.title : '',
          isValid: true
      },
      description:{
          value: place ? place.description : '',
          isValid: true
      }
  },true)
const UpdatePlace:React.FC = () => {
    const {placeId} = useParams();
    const [place,setPlace] = React.useState<Place>();
    const [mount,setMount]=useState(false)
    React.useEffect(() => {
        const place = places.find(p => p.id === placeId);
        setPlace(place);
        setMount(true)
    },[placeId]);
    if(!place){
        return <div>Loading...</div>
    }else{
        return(
            <form className='place-form'>
                <Input id="title" element="input" label='Title'
                    validators={[VALIDATOR_REQUIRE()]}
                    errorText="Please Enter A valid title"
                    onInput={inputHandler}
                    inputValue={formState.inputs.title.value}
                    valid={true}
                    />
                {/* <Input id="address" element="input" label='Address'
                    validators={[VALIDATOR_REQUIRE()]}
                    errorText="Please Enter A valid address"
                    onInput={inputHandler}
                    inputValue={place.address}
                    valid={true}
                    /> */}
                <Input id="description" element="textarea" label='Description'
                    validators={[VALIDATOR_MINLENGTH(5)]}
                    errorText="Please Enter A valid description at least 5 chars"
                    onInput={inputHandler}
                    inputValue={formState.inputs.description.value}
                    valid={true}
                    />
                    <Button type='submit' disabled={true}>
                        Update Place
                    </Button>
            </form> 
        )
    }

}

export default UpdatePlace
