import React from 'react'

import  ReactDOM  from 'react-dom';
import  '../../../styles/header.scss';

interface DrawerProps{
    children?: React.ReactNode;
}

  const drawerHook = document.getElementById("drawer-hook") as HTMLDivElement;
  export const SideDrawerNavigation: React.FC<DrawerProps> = ({
    children,
  }): JSX.Element | null => {
    const [domReady, setDomReady] = React.useState(false)

    React.useEffect(() => {
      setDomReady(true)
    })
    let content = <aside className="side-drawer">{children}</aside>;
    return domReady? ReactDOM.createPortal(content, drawerHook):null;
  };
