import React from "react";
import { Input } from "../shared/UIElements/Input";
import { VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH } from '../../utlis/validations/validator';
import Button from "../shared/UIElements/Button";


const formReducer=(state:any,action:any)=>{
    switch (action.type) {
        case 'INPUT_CHANGE':
            let formIsValid=true;
            for(const inputId in state.inputs){
                if(inputId===action.inputId){
                    formIsValid=formIsValid&&action.isValid
                }else{
                    formIsValid=formIsValid&&state.inputs[inputId].isValid
                }
            }
            return{
                ...state,
                inputs:{
                    ...state.inputs,
                    [action.inputId]:{
                        value:action.value,
                        isValid:action.isValid
                    }
                },
                isValid:formIsValid
            }

        default:
            return state
    }
} 

export const NewPlace: React.FC = () => {
    const [formState,dispatch ]=React.useReducer(formReducer,{
        inputs:{
            title:{
                value:'',
                isValid:false,  
            },
            description:{
                value:'',
                isValid:false,
            }
        },
        isValid:false
    })
    const inputHandler=
            React.useCallback(
                (id:any,value:any,isValid:any)=>{
                    dispatch({
                        type:'INPUT_CHANGE',
                         value,
                         isValid,
                         inputId:id
                    })
                    console.log('id',formState,id)
            },[dispatch])

       const placeSubmitHandler=
                    React.useCallback((event: React.FormEvent<HTMLFormElement>)=>{
                            event.preventDefault();
                            console.log('formState',formState)
                    },[])
    return (
        <form className="place-form" onSubmit={placeSubmitHandler}>
            <Input 
             label="Title" 
             element="input"
             id="title"
             onInput={inputHandler}
             errorText={`please enter valid text`}
             validators={[VALIDATOR_REQUIRE()]}
            />
            <Input 
             label="Description" 
             element="textarea"
             id="description"
             onInput={inputHandler}
             errorText={`please enter at least five characters text`}
             validators={[VALIDATOR_MINLENGTH(5)]}
            />
            <Button type="submit" disabled={!formState.isValid}>
                Add place
            </Button>
        </form>
    );
}
