import React from 'react'
import Card from '../shared/UIElements/Card';
import { Place } from './placesInterFace';

import '../../styles/places.scss'
import Button from '../shared/UIElements/Button';
import Modal from '../shared/UIElements/Modal';
import { Map } from '../shared/UIElements/Map';

const PlaceItem:React.FC<Place> = ({
    id,
    imageUrl,
    title,
    description,
    address,
    creatorId,
    coordinates
}) => {
    const [showMap,setShowMap] = React.useState(false);
    const openMapHandler = () => setShowMap(true);
    const closeMapHandler = () => setShowMap(false);

    const dataProps:any={
        show:showMap,
        onCancel:closeMapHandler,
        header:address,
        contentClass:'place-item__modal-content',
        footerClass:'place-item__modal-actions',
        footer:<Button onClick={closeMapHandler}>CLOSE</Button>,

    }
  return (
  <>
    <Modal {...dataProps}>
        <div className="map-container">
            {/* <Map coordinates={coordinates} /> */}


<iframe title="map" width="100%" height="100%" frameBorder="0" scrolling="no" marginHeight={0} marginWidth={0}

src={'https://maps.google.com/maps?q=' + coordinates.lat.toString() + ',' + coordinates.lng.toString() + '&t=&z=15&ie=UTF8&iwloc=&output=embed'}></iframe><script type='text/javascript' src='https://embedmaps.com/google-maps-authorization/script.js?id=5a33be79e53caf0a07dfec499abf84b7b481f165'></script>
         </div>   
    </Modal>
    <li className='place-item'>
        <Card className='place-item__content'>
        <div className='place-item__image'>
            <img src={imageUrl} alt={title} />
        </div>
        <div className="place-item__info">
            <h2>{title}</h2>
            <h3>{address}</h3>
            <p>{description}</p>
        </div>
        <div className="place-item__actions">
            <Button inverse={true} onClick={openMapHandler}>
            view on map
            </Button>

            <Button to={`/places/${id}`}> edit </Button>
            <Button danger={true}> delete </Button>
        </div>
        </Card>
    </li>
  </>
  )
}

export default PlaceItem
