import React from 'react'
import { actionCreators } from '../../../store';

import './input.scss'

interface InputProps {
    name?: string;
    type?: string;
    value?: string;
    onChange?: (event: React.ChangeEvent<HTMLInputElement|HTMLTextAreaElement|HTMLSelectElement>) => void;
    placeholder?: string;
    className?: string;
    style?: object;
    label?: string;
    labelClass?: string;
    id?: string;
    element?:'input'|'textarea'|'select'
    row?:number
}
const inputReducer=(state:any,action:any)=>{
    switch(action.type){
        case 'CHANGE':
            return {
                ...state,
                value:action.value,
                isValid:true
            }
         default:
            return state   
    }
}
export const Input:React.FC <InputProps> = ({
    name,
    type = 'text',
    value,
    onChange,
    placeholder,
    className,
    style,
    label,
    labelClass,
    id,
    element,
    row=3
}) => {
    let elementType;
   const [inputState,dispatch] =React.useReducer(inputReducer,{value:'',isValid:false});
    const onChangeHandler = (event: React.ChangeEvent<HTMLInputElement|HTMLTextAreaElement|HTMLSelectElement>) => {
        dispatch({
            type:'CHANGE',
            value:event.target.value
        })
    }
    if(element === 'input'){
        elementType = <input 
                            //  name={name} 
                             type={type} 
                             value={inputState.value} 
                             onChange={onChangeHandler} 
                             placeholder={placeholder} 
                            //  className={className} 
                            //  style={style} 
                             id={id} />
    }else if(element === 'textarea'){
        elementType = <textarea 
                            //  name={name} 
                            value={inputState.value} 
                            onChange={onChangeHandler} 
                             placeholder={placeholder} 
                            //  className={className} 
                            //  style={style} 
                             id={id} 
                             rows={row}
                             />
    }else if(element === 'select'){

    }

  return (
    <div className='form-control'>
        <label htmlFor={id}>{label}</label>
        {elementType}
        {inputState}
    </div>
  )
}
